mapper.select:
SELECT ANNOUNCE_TITLE,ANNOUNCE_CONTENT 
FROM ANNOUNCEMENT
WHERE ANNOUNCE_NO = #{announceNo}

mapper.update:
UPDATE ANNOUNCEMENT
SET ANNOUNCE_TITLE = #{title} , ANNOUNCE_CONTENT = #{content}
WHERE ANNOUNCE_NO = #{announceNo}

UPDATE ANNOUNCEMENT
SET ANNOUNCE_DEL_FL ='Y'
WHERE ANNOUNCE_NO = #{announceNo}
