mapper.delete:
DELETE FROM "TB_MEMBER"
WHERE MEMBER_NO = #{memberNo}
AND NAVER_FL = 'Y'

mapper.update:
UPDATE "TB_MEMBER" SET
MEMBER_EMAIL = #{memberEmail},
MEMBER_NICKNAME = #{memberNickname},
MEMBER_TEL = #{memberTel},
MEMBER_GENDER = #{memberGender}
WHERE MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO TB_ADDRESS (
    MEMBER_NO, RECEIVERNAME, POSTCODE, ADDRESS, DETAILADDRESS, PHONE, EXTRAPHONE
) VALUES (
    #{memberNo}, #{receiverName}, #{postcode}, #{address}, #{detailAddress}, #{phone}, #{extraPhone}
)

mapper.select:
SELECT COUNT(*) 
FROM "TB_ADDRESS" 
WHERE MEMBER_NO = #{memberNo} 
AND ADDRESS = #{address} 
AND DETAILADDRESS = #{detailAddress}

mapper.update:
UPDATE "TB_ADDRESS" 
SET BASIC_ADDRESS = 'Y'
WHERE ADDRESS_NO = #{addressNo}
AND MEMBER_NO = #{memberNo}
